import React, { useEffect, useState } from 'react'
import axios from "axios"
import { Link, useNavigate } from 'react-router-dom'
import "../App.css"
import Pagination from '../components/Pagination'

function Books() {
const keys= ["name", "desc"]
  const[books,setbooks]=useState([])
  const [ loading,setLoading]= useState(false)
  const[searchQuery,setsearchQuery]=useState("")
  const[currentpage,setcurrentpage]=useState(1)
  const[postperpage,setpostperpage]=useState(2)

  const lastpostindex= currentpage*postperpage
  const firstpostindex= lastpostindex-postperpage

  async function fetchData(){


  try{
    setLoading(true)
 const res=await axios.get("http://localhost:3001/books")
const display = setbooks(res.data)
const currentposts=books.slice(firstpostindex,lastpostindex)
 setbooks(currentposts)



  }catch(err){
    console.log(err)
  }
}

async function handleDelete(id){
  try{
    await axios.delete("http://localhost:3001/books/"+id)
    window.location.reload()
  }catch(err){
    console.log(err)
  }
}

 
  useEffect(()=>{
  fetchData()
  })
 
  return (
    
    <div className='productsContainer'>



{books
.filter((book)=>{
  if(searchQuery===""){
    return book
  }else if
   
   (keys.some((key)=>book[key].toLowerCase().includes(searchQuery.toLowerCase())))
  
return ( book)
  })

.map((book)=>(
  <div key={book.id} className="products">
  <Link to={`book/${book.id}`}  style={{ textDecoration: 'none' }}>
  <img src={book.image} alt="" />
<p>{book.name}</p>
</Link>

<button onClick={()=>handleDelete(book.id)} className="removeButton"><i class="fa-sharp fa-solid fa-trash"></i></button>

</div>
))}
<Link to="/add"  style={{ textDecoration: 'none' }}>
<button>add</button>
</Link>
<input type="text"  placeholder='search' onChange={(e)=>setsearchQuery(e.target.value)} />
<Pagination totalPosts={books.length} postperpage={postperpage}/>
    </div>

    )
}

export default Books
