import React, { useEffect, useState } from 'react'
import axios from "axios"
import { Link, useNavigate } from 'react-router-dom'
import "../App.css"

function Books() {

  const[books,setbooks]=useState([])
  const[searchQuery,setsearchQuery]=useState("")

  async function fetchData(){
  try{
 const res=await axios.get("http://localhost:3001/books")
 setbooks(res.data)
  }catch(err){
    console.log(err)
  }
}

async function handleDelete(id){
  try{
    await axios.delete("http://localhost:3001/books/"+id)
    window.location.reload()
  }catch(err){
    console.log(err)
  }
}

 
  useEffect(()=>{
  fetchData()
  },[])
 
  return (
    <div className='productsContainer'>

{books
.filter((value)=>{
  if(searchQuery===""){
    return value
  }
}

)




.map((book)=>(
  <div key={book.id} className="products">
  <Link to={`book/${book.id}`}  style={{ textDecoration: 'none' }}>
  <img src={book.image} alt="" />
<p>{book.name}</p>


</Link>
<button onClick={()=>handleDelete(book.id)} className="removeButton"><i class="fa-sharp fa-solid fa-trash"></i></button>

</div>
))}
<Link to="/add"  style={{ textDecoration: 'none' }}>
<button>add</button>
</Link>
<input type="text"  placeholder='search' onChange={(e)=>setnewquery(e.target.value)} />

    </div>
  )
}

export default Books
