import React, { useState,useEffect } from 'react'
import {app} from "./firebase"
import { auth } from './firebase';

import {createUserWithEmailAndPassword , onAuthStateChanged , signInWithEmailAndPassword,
signOut

} from "firebase/auth";


 function App() {

const [registerEmail,setregisterEmail]= useState("")
const [registerpassword,setregisterpassword]= useState("")

const [loginEmail,setloginEmail]= useState("")
const [loginpassword,setloginpassword]= useState("")

const [currentuser,setcurrentUser]= useState({})

useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setcurrentUser(currentUser);
    });

}, [])

function handleRegisterEmail(e){
  setregisterEmail(e.target.value)
}
function handleRegisterPassword(e){
  setregisterpassword(e.target.value)
}


async function register(){
  try{
    const user= await createUserWithEmailAndPassword(
    auth,
      registerEmail,
      registerpassword
    )
    console.log(user)
  }catch(err){
    console.log(err.message)
  }

}
async function login(){
try {
const user= await signInWithEmailAndPassword(
  auth,
  loginEmail,
  loginpassword
)
}catch(err){
  console.log(err.message)
}
}
async function logout(){
await signOut(auth)
}




  return (
    <div className='container'>
    <div className="formContainer">

    <div className="register">
    <h3>Register</h3>
<input type="email"  name='email' placeholder='email' onChange={(e)=>handleRegisterEmail()}/>
<input type="password"  name='password' placeholder='password' onChange={(e)=>handleRegisterPassword}/>
<button onClick={register}>Register</button>
</div>

   <div className="login">
    <h3>login</h3>
<input type="email"  name='email' placeholder='email' onChange={(e)=>setloginEmail({[e.target.name]: e.target.value})}/>
<input type="password"  name='password' placeholder='password' onChange={(e)=>setloginpassword({[e.target.name]: e.target.value})}/>
<button >login</button>
</div>

   <div className="logout">
    <h3>logout</h3>
<h3> User logged in :</h3>
<h3>user email is : {currentuser?.email}</h3>

<button onClick={logout}>logout</button>
</div>







    </div>
    </div>
  )
}

export default App;
