import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { remove,increment,decrement } from '../redux/cartSlice'
import { cartTotalPriceSelector } from '../redux/selectors'
import { cartTotalSelector } from '../redux/selectors'
import "../App.css"

function Cart() {
  const dispatch=useDispatch()
  const totalprice= useSelector(cartTotalPriceSelector)
  const totalquantity= useSelector(cartTotalSelector)
const books= useSelector((state=>state.cart))

function handleRemove(bookid){
  dispatch(remove(bookid))
}

function handleIncrement(bookid){
  dispatch(increment(bookid))
}

function handleDecrement(bookid){
  dispatch(decrement(bookid))
}



if( totalquantity>0){
  return (
    <div >
  {books.map((book)=>{
  const totalperitem= book.quantity*book.price
 return(

  <div className='cartItemInCart'>
<img src={book.image} alt="" />
<h3>{book.name}</h3>
<h3>${book.price}</h3>
<button onClick={()=>handleDecrement(book.id)}>-</button>
<h4>{book.quantity}</h4>
<button onClick={()=>handleIncrement(book.id)}>+</button>
<h3>${totalperitem}</h3>
<button onClick={()=>handleRemove(book.id)}>X</button>
</div>
 )})}

<h3>Total : {totalprice}</h3>
<h3>Quantity: {totalquantity}</h3>
    </div>
    )
}else if (totalquantity===0){
  return (
    <h1>no items in cart</h1>
  )
}


}

export default Cart
