import React, { useState } from 'react'
import axios from "axios"

function App() {

   const [name, setName]= useState("") 
   const [age, setAge]= useState("") 
   const [gender, setGender]= useState("") 
   const [employees,setemployees]= useState([])

function storeName(e){
    setName(e.target.value)
}
function storeAge(e){
    setAge(e.target.value)
}
function storeGender(e){
    setGender(e.target.value)
}

function submitDetails(){
    axios.post("http://localhost:3001/create",{
        name:name,
        age:age,
        gender:gender
    })
    .then(()=>{
        console.log("success")
    })
}

function fetchDetails(){
    axios.get("http://localhost:3001/employees")
    .then((res)=>{
        setemployees(res.data)
    })
}

employees.map((employee)=>{
    return (
        <div key={employee.id}>
            <h3> { employee.name}</h3>
        </div>
    )
})

  return (
    <div>
<label htmlFor="name">Name : </label>
<input type="text"  placeholder='name' name='name' onChange={storeName}/>

<label htmlFor="age">Age : </label>
<input type="number"  placeholder='age' name='age' onChange={storeAge}/>

<label htmlFor="gender">Gender : </label>
<input type="text"  placeholder='gender' name='gender' onChange={storeGender}/>

<button onClick={submitDetails}>submit</button>

<button onClick={fetchDetails}>show employees list</button>


{employees.map((employee)=>{
   
        <div key={employee.id}>
            <h3> { employee.name}</h3>
        </div>
    
})
}









</div>
  )
}

export default App
