import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { remove,increment,decrement } from '../redux/cartSlice'
import "../App.css"

function Cart() {
  const dispatch=useDispatch()
const books= useSelector((state=>state.cart))

function handleRemove(bookid){
  dispatch(remove(bookid))
}

function handleIncrement(bookid){
  dispatch(increment(bookid))
}

function handleDecrement(bookid){
  dispatch(decrement(bookid))
}
  return (
    <div>
{books.map((book)=>(
  <div className='cartItemInCart'>
<img src={book.image} alt="" />
<h3>{book.name}</h3>
<h3>${book.price}</h3>
<button>-</button>
<h3>0</h3>
<button>+</button>
<h3>$100</h3>
<button onClick={()=>handleRemove(book.id)}>X</button>
</div>
))}



<h3>Total : </h3>
    </div>
    
  )
}

export default Cart
